mapper.select:
SELECT g.id AS postId, g.title AS title, g.created_at AS createdAt, 'gallery' AS postType FROM galleries g JOIN users u ON g.writer = u.id WHERE u.username = #{username}
UNION ALL
SELECT f.id AS postId, f.title AS title, f.created_at AS createdAt, 'freeboard' AS postType FROM freeboards f JOIN users u ON f.writer = u.id WHERE u.username = #{username}
ORDER BY createdAt DESC

SELECT c.id AS commentId, c.content AS content, c.created_at AS createdAt, c.gallery AS postId, 'gallery' AS commentType FROM gallery_comments c JOIN users u ON c.writer = u.id WHERE u.username = #{username}
UNION ALL
SELECT fc.id AS commentId, fc.content AS content, fc.created_at AS createdAt, fc.freeboard AS postId, 'freeboard' AS commentType FROM freeboard_comments fc JOIN users u ON fc.writer = u.id WHERE u.username = #{username}
ORDER BY createdAt DESC

mapper.update:
UPDATE users
SET
nickname = #{nickname},
province = #{province},
city = #{city}		
WHERE id = #{id}

UPDATE users
SET image = #{image}
WHERE id = #{id}
