mapper.select:




        ORDER BY r.club_id ${sortOrder}


        ORDER BY r.meeting_date ${sortOrder}


        ORDER BY r.id ${sortOrder}


LIMIT #{limit} OFFSET #{offset}

SELECT *
FROM recruitments
WHERE club_id = #{clubId} AND DATE(meeting_date) = #{date}

SELECT user_id
FROM recruitment_member
WHERE recruitment_id = #{recruitmentId}

SELECT COUNT(*)
FROM recruitments
WHERE status = #{status}
